import React, { useState, useEffect } from 'react';
import axios from 'axios';
import pen from "../assets/pen.png"
import trash from "../assets/trash can.png"
import { Link } from 'react-router-dom';


const AdminPanel = () => {
  const [users, setUsers] = useState([]);
  const [newUsername, setNewUsername] = useState('');
  const [newPassword, setNewPassword] = useState('');
  const [newIsAdmin, setNewIsAdmin] = useState(false);
  const [newIsYetkili, setNewIsYetkili] = useState(false);
  const [anket, setanket] = useState([]);
  useEffect(() => {
    // Kullanıcıları yükleme işlemi
    loadUsers();
    loadanket();
  
  
  }, []);


  const loadUsers = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/users');
      setUsers(response.data.users);
    } catch (error) {
      console.error('Kullanıcılar yüklenirken bir hata oluştu:', error);
    }
  };

  const loadanket = async () => {
    try {
      const response = await axios.get('http://localhost:5000/anketget');
      setanket(response.data.anket);
    } catch (error) {
      console.error('anket yüklenirken bir hata oluştu:', error);
    }
  };


  const deleteUser = async (userId) => {
    try {
      console.log(userId);
      await axios.delete(`http://localhost:5000/api/users/${userId}`);
      // Kullanıcıları güncelleme
      loadUsers();
    } catch (error) {
      console.error('Kullanıcı silinirken bir hata oluştu:', error);
    }
  };

  const handleUserAdd = async () => {
    try {
      const response = await axios.post('http://localhost:5000/api/users', {
        username: newUsername,
        password: newPassword,
        is_admin: newIsAdmin,
        is_yetkili: newIsYetkili,
      });
      setUsers([ response.data.user]);
      setNewUsername('');
      setNewPassword('');
      setNewIsAdmin(false);
      setNewIsYetkili(false);
    } catch (error) {
      console.error('Kullanıcı eklenirken bir hata oluştu:', error);
    }
  };
  const handleUserUpdate = async (userId, isAdmin, isYetkili) => {
    try {
      await axios.put(`http://localhost:5000/api/users/${userId}`, {
        is_admin: isAdmin,
        is_yetkili: isYetkili,
      });
      // Kullanıcıları güncelleme
      loadUsers();
    } catch (error) {
      console.error('Kullanıcı güncellenirken bir hata oluştu:', error);
    }
  };

  return (
    <>
      <div className='center'>
        <h1 className='center'>Admin Paneli</h1>
       
        <div><br />
          <h3>Kullanıcı Ekle</h3>
          <div>
            <input
              type="text"
              placeholder="Kullanıcı Adı"
              value={newUsername}
              onChange={(e) => setNewUsername(e.target.value)}
            />
            <input
              type="password"
              placeholder="Şifre"
              value={newPassword}
              onChange={(e) => setNewPassword(e.target.value)}
            />
            <br />
            <label className='altbaslik'>
              Admin Yetkisi:
              <input
                type="checkbox"
                checked={newIsAdmin}
                onChange={(e) => setNewIsAdmin(e.target.checked)}
              />
            </label><br />
            <label className='altbaslik'>
              Yetkili Yetkisi:
              <input
                type="checkbox"
                checked={newIsYetkili}
                onChange={(e) => setNewIsYetkili(e.target.checked)}
              />
            </label><br />
            <button className='btn btn-success' onClick={handleUserAdd}>Yeni Kullanıcı Ekle</button>
          <br />
          
          </div>
          <br />
          <p id='mevcutyetkili' className='baslik'>Mevcut Yetkililer</p>
          
          <table className='minibaslik center'>
    <thead>
      <tr>
        <th className='tabloBasligi'>Kullanıcı</th>
        <th className='tabloBasligi'>Admin Yetkisi</th>
        <th className='tabloBasligi'>Yetkili Yetkisi</th>
        <th className='tabloBasligi icerik'>İşlemler</th>
      </tr>
    </thead>
    <tbody>
  {anket && anket.length > 0 ? (
    anket.map((complaint) => (
      <tr key={complaint.id}>
        <td className='tabloİcerik'>{complaint.username}</td>
        <td className='tabloİcerik'>{complaint.is_admin}</td>
        <td className='tabloİcerik'>{complaint.is_yetkili}</td>
      </tr>
    ))
  ) : (
    <tr>
      <td colSpan="3">Anket verisi bulunmamaktadır.</td>
    </tr>
  )}
</tbody>

  </table>
  
  
        </div>
        <br />
        <br />
        <Link className='buton' to="../AdminSurveys">Anketler Sayfasına Git</Link>
        <h2> Anketler</h2>
        <table className='tablo'>
    <thead>
      <tr>
        <th className='tabloBasligi'>Memnuniyet Durumu</th>
        <th className='tabloBasligi'>Kullanıcı Notu</th>
        <th className='tabloBasligi'>Şikayet Başlığı</th>
      </tr>
    </thead>
    <tbody>
  {anket && anket.length > 0 ? (
    anket.map((complaint) => (
      <tr key={complaint.id}>
        <td className='tabloİcerik'>{complaint.memnuniyet}</td>
        <td className='tabloİcerik'>{complaint.aciklama}</td>
        <td className='tabloİcerik'>{complaint.title}</td>
      </tr>
    ))
  ) : (
    <tr>
      <td colSpan="3">Anket verisi bulunmamaktadır.</td>
    </tr>
  )}
</tbody>

  </table>
  
        
      </div>
      </>
    );
  };
  
  export default AdminPanel;
