import React, { useState, useEffect } from 'react';
import axios from 'axios';

const AdminSurveys = () => {
  const [surveys, setSurveys] = useState([]);
  const [newSurveyQuestion, setNewSurveyQuestion] = useState('');
  const [newSurveyOptions, setNewSurveyOptions] = useState('');

  useEffect(() => {
    axios.get('http://localhost:5000/admin/surveys').then((response) => {
      setSurveys(response.data);
    });
  }, []);

  const handleSurveyAdd = () => {
    const optionsArray = newSurveyOptions.split(',').map((option) => option.trim());
    axios
      .post('http://localhost:5000/admin/surveys', {
        question: newSurveyQuestion,
        options: optionsArray,
      })
      .then((response) => {
        setSurveys([...surveys, response.data]);
        setNewSurveyQuestion('');
        setNewSurveyOptions('');
      });
  };

  const handleSurveyDelete = (surveyId) => {
    axios.delete(`http://localhost:5000/admin/surveys/${surveyId}`).then(() => {
      setSurveys(surveys.filter((survey) => survey.id !== surveyId));
    });
  };

  return (
    <div>
      <h2>Anketleri Yönet</h2>
      <div>
        <input
          type="text"
          placeholder="Soru"
          value={newSurveyQuestion}
          onChange={(e) => setNewSurveyQuestion(e.target.value)}
        />
        <input
          type="text"
          placeholder="Seçenekler (virgülle ayrılmış)"
          value={newSurveyOptions}
          onChange={(e) => setNewSurveyOptions(e.target.value)}
        />
        <button onClick={handleSurveyAdd}>Anket Ekle</button>
      </div>
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Soru</th>
            <th>Seçenekler</th>
            <th>İşlemler</th>
          </tr>
        </thead>
        <tbody>
          {surveys.map((survey) => (
            <tr key={survey.id}>
              <td>{survey.id}</td>
              <td>{survey.question}</td>
              <td>{survey.options.join(', ')}</td>
              <td>
                <button onClick={() => handleSurveyDelete(survey.id)}>Sil</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default AdminSurveys;
