import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const OpenComplaints = () => {
  const [complaints, setComplaints] = useState([]);
  const [usernames, setUsernames] = useState({});

  useEffect(() => {
    const getOpenComplaints = async () => {
      try {
        const response = await axios.get('http://localhost:5000/complaints/open');
        setComplaints(response.data.complaints);
      } catch (error) {
        console.error(error);
      }
    };
    getOpenComplaints();
  }, []);

  useEffect(() => {
    const fetchUsernames = async () => {
      const usernamesData = {};
      for (const complaint of complaints) {
        try {
          const response = await axios.get(`http://localhost:5000/user/${complaint.user_id}`);
          usernamesData[complaint.user_id] = response.data.username;
        } catch (error) {
          console.error(error);
          usernamesData[complaint.user_id] = 'Bilinmeyen Kullanıcı';
        }
      }
      setUsernames(usernamesData);
    };

    if (complaints.length > 0) {
      fetchUsernames();
    }
  }, [complaints]);

  return (
    <div>
      <h1>Açık Arızalar</h1>
      <table>
        <thead>
          <tr>
            <th>Arıza No</th>
            <th>Başlık</th>
            <th>Kaydı Oluşturan Kullanıcı</th>
            <th>Statü</th>
            <th>Detayları Gör</th>
          </tr>
        </thead>
        <tbody>
          {complaints.map((complaint) => (
  <tr key={complaint.id}>
    <td>{complaint.id}</td>
    <td>{complaint.title}</td>
    <td>{usernames[complaint.user_id]}</td>
    <td>{complaint.status}</td>
    <td>
      <Link to={`/complaint/${complaint.id}`}>Detayları Gör</Link>
    </td>
  </tr>
))}
        </tbody>
      </table>
    </div>
  );
};

export default OpenComplaints;
