import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const OpenComplaints = () => {
  const [complaints, setComplaints] = useState([]);

  useEffect(() => {
    const getOpenComplaints = async () => {
      try {
        const response = await axios.get('http://localhost:5000/complaints/open');
        setComplaints(response.data.complaints);
      } catch (error) {
        console.error(error);
      }
    };
    getOpenComplaints();
  }, []);

  // Kullanıcı adlarını döndüren fonksiyon
  const getUsername = async (userId) => {
    try {
      const response = await axios.get(`http://localhost:5000/user/${userId}`);
      return response.data.username;
    } catch (error) {
      console.error(error);
      return '';
    }
  };

  return (
    <div>
      <h1>Açık Arızalar</h1>
      <table>
        <thead>
          <tr>
            <th>Arıza No</th>
            <th>Başlık</th>
            <th>Kaydı Oluşturan Kullanıcı</th>
            <th>Statü</th>
            <th>Detayları Gör</th>
          </tr>
        </thead>
        <tbody>
          {complaints.map(async (complaint) => {
            const username = await getUsername(complaint.user_id);
            return (
              <tr key={complaint.id}>
                <td>{complaint.id}</td>
                <td>{complaint.title}</td>
                <td>{username}</td>
                <td>{complaint.status}</td>
                <td>
                  <Link to={`/complaint/${complaint.id}`}>Detayları Gör</Link>
                </td>
              </tr>
            );
          })}
        </tbody>
      </table>
    </div>
  );
};

export default OpenComplaints;
