import React, { useState } from 'react';
import LoginForm from './components/LoginForm';
import RegisterForm from './components/RegisterForm';
import ComplaintForm from './components/ComplaintForm';
import OpenComplaints from './components/OpenComplaints';
import SolvedComplaints from './components/SolvedComplaints';
import ViewComplaint from './components/ViewComplaint';
import AdminPanel from './components/AdminPanel';

const App = () => {
  const [isLogin, setIsLogin] = useState(true);
  const [isAdmin, setIsAdmin] = useState(false); // isAdmin değişkenini tanımlama
  const [message, setMessage] = useState('');

  // Bildirim fonksiyonunu tanımlayın
  const notify = (message) => {
    setMessage(message);
  };

  return (
    <div className="app-container">
      {isLogin ? (
        isAdmin ? (
          <AdminPanel />
        ) : (
          <>
            <h1>Arıza Kaydı Oluştur</h1>
            <ComplaintForm />
            <h1>Açık Arıza Kayıtları</h1>
            <OpenComplaints />
            <h1>Çözümlenmiş Arıza Kayıtları</h1>
            <SolvedComplaints />
          </>
        )
      ) : (
        <>
          <h1>Giriş Yap</h1>
          <LoginForm notify={notify} />
          <h1>Kayıt Ol</h1>
          <RegisterForm notify={notify} />
        </>
      )}
      <button onClick={() => setIsLogin(!isLogin)}>
        {isLogin ? 'Çıkış Yap' : 'Giriş Yap'}
      </button>
    </div>
  );
};

export default App;
