import React, { useState } from 'react';
import axios from 'axios';

const RegisterForm = ({ setIsLogin, notify }) => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleRegister = () => {
    axios
      .post('http://localhost:5000/register', { username, password })
      .then((response) => {
        notify(response.data.message); // Backend'den dönen mesajı bildirim olarak göster
        setIsLogin(true); // Kayıt olduktan sonra otomatik olarak giriş ekranına yönlendir
      })
      .catch((error) => {
        notify('Kayıt başarısız'); // Hata durumunda bildirim olarak göster
      });
  };

  return (
    <div>
      <input
        type="text"
        placeholder="Kullanıcı Adı"
        value={username}
        onChange={(e) => setUsername(e.target.value)}
      />
      <input
        type="password"
        placeholder="Şifre"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
      />
      <button onClick={handleRegister}>Kayıt Ol</button>
    </div>
  );
};

export default RegisterForm;
