import React, { useState } from 'react';
import LoginForm from './components/LoginForm';
import RegisterForm from './components/RegisterForm';
import RecordForm from './components/RecordForm'; // Import RecordForm component
import axios from 'axios'; // Import axios for making requests

const App = () => {
  const [isLogin, setIsLogin] = useState(true);
  const [message, setMessage] = useState('');

  // Bildirim fonksiyonunu tanımlayın
  const notify = (message) => {
    setMessage(message);
  };

  // Record kaydetme işlevi
  const handleRecordSubmit = (data) => {
    axios.post('http://localhost:5000/ariza_k', data)
      .then((response) => {
        notify(response.data.message); // Backend'den dönen mesajı bildirim olarak göster
      })
      .catch((error) => {
        notify('Kayıt başarısız'); // Hata durumunda bildirim olarak göster
      });
  };

  return (
    <div className="app-container">
      <h1>{isLogin ? 'Giriş Yap' : 'Kayıt Ol'}</h1>
      {/* Bildirimi mesaj kutusu olarak göster */}
      {message && <div>{message}</div>}
      {isLogin ? <LoginForm notify={notify} /> : <RegisterForm notify={notify} />}
      
      {/* RecordForm bileşenini burada kullanın */}
      {!isLogin && <RecordForm onSubmit={handleRecordSubmit} />}

      <button onClick={() => setIsLogin(!isLogin)}>
        {isLogin ? 'Kayıt Ol' : 'Giriş Yap'}
      </button>
    </div>
  );
};

export default App;
