// frontend/src/components/ComplaintForm.js
import React, { useState } from 'react';
import axios from 'axios';

const ComplaintForm = ({ notify, user }) => {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [address, setAddress] = useState('');

  const handleComplaintSubmit = () => {
    axios
      .post('http://localhost:5000/complaints', {
        title,
        description,
        address,
        user_id: user.id // Şikayeti oluşturan kullanıcının ID'sini gönderiyoruz
      })
      .then((response) => {
        notify(response.data.message); // Backend'den dönen mesajı bildirim olarak göster
      })
      .catch((error) => {
        notify('Şikayet oluşturulamadı'); // Hata durumunda bildirim olarak göster
      });
  };

  return (
    <div>
      <input
        type="text"
        placeholder="Başlık"
        value={title}
        onChange={(e) => setTitle(e.target.value)}
      />
      <textarea
        placeholder="Açıklama"
        value={description}
        onChange={(e) => setDescription(e.target.value)}
      />
      <input
        type="text"
        placeholder="Adres"
        value={address}
        onChange={(e) => setAddress(e.target.value)}
      />
      <button onClick={handleComplaintSubmit}>Şikayet Oluştur</button>
    </div>
  );
};

export default ComplaintForm;
