// App.js
import React, { useState } from 'react';
import LoginForm from './components/LoginForm';
import RegisterForm from './components/RegisterForm';
import ComplaintForm from './components/ComplaintForm';

const App = () => {
  const [isLogin, setIsLogin] = useState(true);
  const [message, setMessage] = useState('');
  const [loggedInUser, setLoggedInUser] = useState(null);

  // Bildirim fonksiyonunu tanımlayın
  const notify = (message) => {
    setMessage(message);
  };

  // Kullanıcı girişini kontrol edin
  const handleLogin = (user) => {
    setLoggedInUser(user);
  };

  return (
    <div className="app-container">
      <h1>{isLogin ? 'Giriş Yap' : 'Kayıt Ol'}</h1>
      {/* Bildirimi mesaj kutusu olarak göster */}
      {message && <div>{message}</div>}
      {isLogin ? (
        <LoginForm notify={notify} handleLogin={handleLogin} />
      ) : (
        <RegisterForm notify={notify} handleLogin={handleLogin} />
      )}

      {/* Giriş yapıldıysa veya kayıt olunduysa ComplaintForm'u göster */}
      {loggedInUser && <ComplaintForm notify={notify} user={loggedInUser} />}

      <button onClick={() => setIsLogin(!isLogin)}>
        {isLogin ? 'Kayıt Ol' : 'Giriş Yap'}
      </button>
    </div>
  );
};

export default App;
