// frontend/src/components/RegisterForm.js
import React, { useState } from 'react';
import axios from 'axios';
import ComplaintForm from './ComplaintForm';

const RegisterForm = ({ notify }) => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [loggedInUser, setLoggedInUser] = useState(null);

  const handleRegister = () => {
    axios
      .post('http://localhost:5000/register', { username, password })
      .then((response) => {
        notify(response.data.message);
        if (response.data.message === 'Kayıt başarılı') {
          setLoggedInUser(response.data.user); // Kullanıcının giriş yaptığını belirtiyoruz
        }
      })
      .catch((error) => {
        notify('Kayıt başarısız');
      });
  };

  return (
    <div>
      {loggedInUser ? (
        <ComplaintForm notify={notify} user={loggedInUser} /> // Giriş yapıldıysa ComplaintForm'u göster
      ) : (
        <>
          <input
            type="text"
            placeholder="Kullanıcı Adı"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
          <input
            type="password"
            placeholder="Şifre"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
          <button onClick={handleRegister}>Kayıt Ol</button>
        </>
      )}
    </div>
  );
};

export default RegisterForm;
