import React, { useEffect, useState } from 'react';
import axios from 'axios';

const ComplaintDetail = ({ match }) => {
  const [complaint, setComplaint] = useState(null);
  const [status, setStatus] = useState('');
  const [note, setNote] = useState('');
  const [message, setMessage] = useState('');

  const complaintId = match.params.id;

  useEffect(() => {
    const getComplaintDetail = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/complaint/${complaintId}`);
        setComplaint(response.data.complaint);
      } catch (error) {
        console.error(error);
      }
    };
    getComplaintDetail();
  }, [complaintId]);

  const handleStatusChange = async () => {
    try {
      await axios.post(`http://localhost:5000/complaint/${complaintId}/status`, {
        status,
        note,
      });
      setMessage('Statü güncellendi.');
    } catch (error) {
      console.error(error);
      setMessage('Statü güncellenemedi.');
    }
  };

  return (
    <div>
      {complaint ? (
        <div>
          <h1>{complaint.title}</h1>
          <p>Açıklama: {complaint.description}</p>
          <p>Açık Adres: {complaint.address}</p>
          <p>Statü: {complaint.status}</p>
          {complaint.status === 'Cozumlendi' && complaint.user_satisfaction ? (
            <div>
              <h2>Kullanıcı Memnuniyet Anketi</h2>
              <p>Memnuniyet: {complaint.user_satisfaction.satisfaction}</p>
              <p>Açıklama: {complaint.user_satisfaction.note}</p>
            </div>
          ) : (
            <div>
              <h2>Statü Güncelle</h2>
              <div>
                <label>Yeni Statü:</label>
                <select value={status} onChange={(e) => setStatus(e.target.value)}>
                  <option value="Cozulmedi">Çözülmedi</option>
                  <option value="Cozuldu">Çözüldü</option>
                </select>
              </div>
              <div>
                <label>Not:</label>
                <textarea value={note} onChange={(e) => setNote(e.target.value)} />
              </div>
              <button onClick={handleStatusChange}>Güncelle</button>
            </div>
          )}
          {message && <p>{message}</p>}
        </div>
      ) : (
        <p>Arıza kaydı bulunamadı.</p>
      )}
    </div>
  );
};

export default ComplaintDetail;
