import React, { useState } from 'react';
import LoginForm from './components/LoginForm';
import RegisterForm from './components/RegisterForm';

const App = () => {
  const [isLogin, setIsLogin] = useState(true);

  // Kayıt olma işlemini yapan fonksiyon
  const handleRegister = async (username, password) => {
    const response = await fetch('/register', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ username, password }),
    });

    const data = await response.json();
    alert(data.message); // Başarılı veya başarısız mesajını göstermek için
  };

  // Giriş yapma işlemini yapan fonksiyon
  const handleLogin = async (username, password) => {
    const response = await fetch('/login', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ username, password }),
    });

    const data = await response.json();
    alert(data.message); // Başarılı veya başarısız mesajını göstermek için
  };

  return (
    <div className="app-container">
      <h1>{isLogin ? 'Giriş Yap' : 'Kayıt Ol'}</h1>
      {isLogin ? <LoginForm handleLogin={handleLogin} /> : <RegisterForm handleRegister={handleRegister} />}
      <button onClick={() => setIsLogin(!isLogin)}>
        {isLogin ? 'Kayıt Ol' : 'Giriş Yap'}
      </button>
    </div>
  );
};

export default App;
