import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import Home from './components/Home';
import LoginPage from './components/LoginPage';
import RegisterPage from './components/RegisterPage';
import AdminPanel from './components/AdminPanel';
import OpenComplaints from './components/OpenComplaints';
import SolvedComplaints from './components/SolvedComplaints';
import UserComplaints from './components/UserComplaints';
import CreateComplaint from './components/CreateComplaint';

const App = () => {
  // Kullanıcı giriş bilgisini tutmak için state ve handler'ı tanımlıyoruz.
  const [user, setUser] = useState(null);

  // Kullanıcının giriş yapmasını sağlayacak fonksiyonu tanımlıyoruz.
  const handleLogin = (userData) => {
    setUser(userData);
  };

  return (
    <Router>
      <div>
        <nav>
          <ul>
            <li>
              <Link to="/">Ana Sayfa</Link>
            </li>
            <li>
              <Link to="/register">Kayıt Ol</Link>
            </li>
            <li>
              <Link to="/login">Giriş yap</Link>
            </li>
            <li>
              <Link to="/admin">Admin Paneli</Link>
            </li>
            <li>
              <Link to="/open-complaints">Açık Arızalar</Link>
            </li>
            <li>
              <Link to="/solved-complaints">Çözümlenmiş Arızalar</Link>
            </li>
            <li>
              <Link to="/user-complaints">Kullanıcı Arızaları</Link>
            </li>
            <li>
              <Link to="/create-complaint">Arıza Oluştur</Link>
            </li>
          </ul>
        </nav>

        {/* Kullanıcı giriş bilgisini LoginPage componentine handleLogin prop'u ile geçiriyoruz. */}
        <Routes>
          <Route
            path="/"
            element={<Home user={user} />} // Home componentine user prop'u ile kullanıcı bilgisini geçiyoruz.
          />
          <Route
            path="/register"
            element={<RegisterPage />}
          />
          <Route
            path="/login"
            element={<LoginPage handleLogin={handleLogin} />} // LoginPage componentine handleLogin prop'u ile fonksiyonu geçiriyoruz.
          />
          <Route
            path="/admin"
            element={<AdminPanel />}
          />
          <Route
            path="/open-complaints"
            element={<OpenComplaints />}
          />
          <Route
            path="/solved-complaints"
            element={<SolvedComplaints />}
          />
          <Route
            path="/user-complaints"
            element={<UserComplaints />}
          />
          <Route
            path="/create-complaint"
            element={<CreateComplaint />}
          />
        </Routes>
      </div>
    </Router>
  );
};

export default App;
