import React, { useState } from 'react';
import axios from 'axios';
import { useLocation, useNavigate } from 'react-router-dom';

const CreateComplaint = () => {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [address, setAddress] = useState('');
  const [message, setMessage] = useState('');

  const location = useLocation();
  const navigate = useNavigate();

  const userId = location.state?.userId || null; // Kullanıcının ID'sini alın

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!userId) {
      setMessage('Giriş yapmadan arıza kaydı oluşturamazsınız.');
      return;
    }
    try {
      const response = await axios.post('http://localhost:5000/complaint', {
        user_id: userId,
        title,
        description,
        address,
      });
      setMessage(response.data.message);
    } catch (error) {
      console.error(error);
      setMessage('Arıza kaydı oluşturulamadı.');
    }
  };

  return (
    <div>
      <h1>Arıza Oluştur</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Başlık:</label>
          <input type="text" value={title} onChange={(e) => setTitle(e.target.value)} />
        </div>
        <div>
          <label>Açıklama:</label>
          <textarea value={description} onChange={(e) => setDescription(e.target.value)} />
        </div>
        <div>
          <label>Açık Adres:</label>
          <input type="text" value={address} onChange={(e) => setAddress(e.target.value)} />
        </div>
        <button type="submit">Kayıt Oluştur</button>
      </form>
      {message && <p>{message}</p>}
    </div>
  );
};

export default CreateComplaint;
