import React, { useState, useEffect } from 'react';
import axios from 'axios';

const AdminPanel = () => {
  const [users, setUsers] = useState([]);
  const [surveys, setSurveys] = useState([]);

  useEffect(() => {
    // Kullanıcıları ve anketleri yükleme işlemi
    loadUsers();
    loadSurveys();
  }, []);

  const loadUsers = async () => {
    try {
      const response = await axios.get('/api/users');
      setUsers(response.data.users);
    } catch (error) {
      console.error('Kullanıcılar yüklenirken bir hata oluştu:', error);
    }
  };

  const loadSurveys = async () => {
    try {
      const response = await axios.get('/api/surveys');
      setSurveys(response.data.surveys);
    } catch (error) {
      console.error('Anketler yüklenirken bir hata oluştu:', error);
    }
  };

  const deleteUser = async (userId) => {
    try {
      await axios.delete(`/api/users/${userId}`);
      // Kullanıcıları güncelleme
      loadUsers();
    } catch (error) {
      console.error('Kullanıcı silinirken bir hata oluştu:', error);
    }
  };

  const deleteSurvey = async (surveyId) => {
    try {
      await axios.delete(`/api/surveys/${surveyId}`);
      // Anketleri güncelleme
      loadSurveys();
    } catch (error) {
      console.error('Anket silinirken bir hata oluştu:', error);
    }
  };

  return (
    <div>
      <h1>Admin Paneli</h1>
      <h2>Kullanıcılar</h2>
      <ul>
        {users.map((user) => (
          <li key={user.id}>
            {user.username} - {user.is_admin ? 'Admin' : 'Normal Kullanıcı'}
            <button onClick={() => deleteUser(user.id)}>Kullanıcıyı Sil</button>
          </li>
        ))}
      </ul>

      <h2>Anketler</h2>
      <ul>
        {surveys.map((survey) => (
          <li key={survey.id}>
            {survey.question} - {survey.options.join(', ')}
            <button onClick={() => deleteSurvey(survey.id)}>Anketi Sil</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default AdminPanel;
