import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

const ComplaintDetail = ({ is_yetkili }) => {
  const [complaint, setComplaint] = useState(null);
  const [status, setStatus] = useState('');
  const [note, setNote] = useState('');
  const [message, setMessage] = useState('');
  const [aciklama, setAciklama] = useState('');
  const [memnuniyet, setMemnuniyet] = useState('');
  const { complaint_id } = useParams();

  useEffect(() => {
    const getComplaintDetail = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/complaint/${complaint_id}`);
        setComplaint(response.data);
      } catch (error) {
        console.error(error);
      }
    };
    getComplaintDetail();
  }, [complaint_id]);

  const handleStatusChange = async () => {
    try {
      await axios.post(`http://localhost:5000/complaint/${complaint_id}/status`, {
        status,
        note,
      });
      setMessage('Statü güncellendi.');
    } catch (error) {
      console.error(error);
      setMessage('Statü güncellenemedi.');
    }
  };

  const handleStatusChange2 = async () => {
    try {
      await axios.post(`http://localhost:5000/complaint/${complaint_id}/den`, {
        memnuniyet,
        aciklama,
      });
      setMessage('Anket gönderildi.');
    } catch (error) {
      console.error(error);
      setMessage('Anket gönderilmedi.');
    }
  };
    console.log(complaint);
    if (complaints.length === 0) {
      return <p>Veriler yükleniyor...</p>;
    }
  
    return (
      <div>
        {complaints.map((complaint) => (
          <div key={complaint.id}>
            <h1>{complaint.title}</h1>
            <p>Açıklama: {complaint.description}</p>
            <p>Açık Adres: {complaint.address}</p>
            <p>Statü: {complaint.status}</p>
            <p>{complaint.note}</p>
            
            {complaint.anket && complaint.status === 'Cozumlendi' ? (
              <div>
                <h2>Kullanıcı Memnuniyet Anketi</h2>
                <p>Memnuniyet: {complaint.memnuniyet || "Henüz belirtilmedi"}</p>
                <p>Açıklama: {complaint.aciklama || "Henüz belirtilmedi"}</p>
              </div>
            ) : null}
    
            {/* Kullanıcı yetkiliyse ve şikayet durumu "Açık" ise */}
            {is_yetkili && complaint.status === 'Acik' ? (
              <>
                <h2>Statü Güncelle</h2>
                <div>
                  <label>Yeni Statü:</label>
                  <select value={complaint.status} onChange={(e) => setComplaints({ ...complaint, status: e.target.value })}>
                    <option value="Acik">Çözülmedi</option>
                    <option value="Cozumlendi">Cozumlendi</option>
                  </select>
                  <div>
                    <label>Not:</label>
                    <textarea value={complaint.note} onChange={(e) => setComplaints({ ...complaint, note: e.target.value })} />
                  </div>
                  <button onClick={handleStatusChange}>Güncelle</button>
                </div>
              </>
            ) : null}
          </div>
        ))}
      </div>
    );
  };
  
  export default ComplaintDetail;
